import React, { useState, useEffect } from "react";
import Nav from "./Nav";
import Footer from "./Footer";
import { connect } from "react-redux";
import { Link, Redirect } from "react-router-dom";
import "../stylesheets/ProjectDetail.scss";
import projects from "../data/data";
import styled from "styled-components";
import { ThemeProvider } from "styled-components";
import themes from "../data/themes";

export function ProjectDetail(props) {

/*   const [destinationIndex, setDestinationIndex] = useState(0);
  const [theme, setTheme] = useState(themes.bread);
  const destination = props.destinations[destinationIndex]

  useEffect(() => {
    setTheme(themes[destination.theme]);
  }, [destination]); */

  return (
    <ThemeProvider theme={theme}  className="projectdetail--wrapper">
      <Nav  {...props}
          destinationIndex={destinationIndex}
          setDestinationIndex={setDestinationIndex} />

      <div className="projectdetail--header">
          <img
            className="projectdetail--img"
            src={props.project.image}
            alt="project"
          />
      </div>
      <div className="projectdetail_title">
          <h1 className="">{destination.title}</h1>
        </div>
        <div className="projects--items">
          {projects.map((item) => (
            <div className="projects--item">

                <img
                  className="list--project_img"
                  src={item.image}
                  alt="project"
                />

            </div>
          ))}
        </div>
      <Footer />
    </ThemeProvider>
  );
}

const mapStateToProps = (state) => {
  return {
    project: state.selected.project,
    mode: state.mode.bread,
  };
};

export default connect(mapStateToProps)(ProjectDetail);
