    <nav className={`nav--wrapper ${scrollStyle} ${shadowStyle}`}>
      <div className="nav--items">
        <Link to="/projects" className="nav--links">
          Projects
        </Link>
      </div>
      <div className="nav--items">
        <Link to="/" className="">
          Ahoi
        </Link>
      </div>
      <div className="nav--items">
        <Link to="/about" className="nav--links">
          About
        </Link>
      </div>
    </nav>
  );
}
