import projects from "../data/data";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import "../stylesheets/Projects.scss";
import React, { useState, useEffect, useMemo } from "react";

/* const categorylist = [
  { id: 1, category: "all", value: "all" },
  { id: 2, category: "poster", value: "all" },
  { id: 3, category: "code", value: "all" },
  { id: 4, category: "editorial", value: "all" },
]; */

export function Projects(props) {
  /* const [filter, setFilter] = useState("all");

  const filteredPortfolio = useMemo(() => {
    if (filter === "all") return projects;
    else return projects.filter((portfolio) => portfolio.category === filter);
  }, [filter]);

  return (
    <div>
      <div className="portfolio-nav">
        {categorylist.map((types) => (
          <div>
            <a onClick={() => setFilter(types.category)}>{types.category}</a>
          </div>
        ))}
      </div>

      <div>
        {filteredPortfolio.map((item) => (
          <div key={item.id}>
            <div item={item.title} />
          </div>
        ))}
      </div>
    </div>
  );
} */
  const [filter, setFilter] = useState("all");
  const [projectlist, setProjects] = useState([]);

  useEffect(() => {
    setProjects(projects);
  }, []);

  useEffect(() => {
    setProjects([]);

    const filtered = projects.filter((p) => p.category.includes(filter));
    console.log(filtered);
    setProjects([...filtered]);
  }, [filter]);

  return (
    <div className="projects--wrapper">
      <p className="nav--text">
        I am <a href="/#">afraid</a> that our eyes are bigger than our stomachs,
        and that we have more <a href="/#">curiosity</a> than understanding. We
        grasp at everything, but catch <a href="/#">nothing</a> except wind.
      </p>
      <div className="project--filter">
        <a href="/#" active={filter === "all"} onClick={() => setFilter("all")}>
          all
        </a>
        <a
          href="/#"
          active={filter === "poster"}
          onClick={() => setFilter("poster")}
        >
          poster
        </a>
        <a
          href="/#"
          active={filter === "code"}
          onClick={() => setFilter("code")}
        >
          code
        </a>
        <a
          href="/#"
          active={filter === "editorial"}
          onClick={() => setFilter("editorial")}
        >
          editorial
        </a>
      </div>
      <div className="projects--items">
        {projectlist.map((item) =>
          item.filtered === true ? (
            <div key={item.title} className="projects--item">
              <div className="project--title_text">{item.title}</div>
              <div className="project--title_text">{item.title}</div>
              <div className="project--title_text">{item.title}</div>

              <div className="list--projects_right">
                <img
                  className="list--project_img"
                  src={item.image}
                  alt="project"
                />
              </div>
            </div>
          ) : (
            ""
          )
        )}
      </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    project: state.selected.project,
  };
};

export default connect(mapStateToProps)(Projects);
