import React, { useLayoutEffect, useRef, useState } from "react";
import styled from "styled-components";
import bread from "../data/bread";
import "../stylesheets/HomeBread.scss";
import { Link } from "react-router-dom";



const Homebread = () => {

const [show, doShow] = useState({
  imgOne: false,
  imgTwo: false,
  imgThree: false,
})

const ourRef =useRef(null),
anotherRef = useRef(null),
refThree = useRef(null);

useLayoutEffect(() => {
  const topPos = element => element.getBoundingClientRect().top;
  const div1Pos =topPos(ourRef.current),
  div2Pos = topPos(anotherRef.current),
  div3Pos =topPos(refThree.current);

  const onScroll = () => {
    const scrollPos = window.ScrollY + window.innerHeight;
    if (div1Pos < scrollPos) {
      doShow(state => ({...state, imgOne: true}));
    } else if (div2Pos < scrollPos) {
      doShow(state => ({...state, imgTwo: true}));
    } else if (div2Pos < scrollPos) {
    doShow(state => ({...state, imgTwo: true}));
  }
  }

  window.addEventListener("scroll", onScroll);
  return () => window.removeEventListener("scroll", onScroll);
}, []);

  return (
    <div className="homebread--wrapper">
      <div className="homebread--intro">
        <p>Bread &</p>
        <span>commissioned projects</span>
      </div>
      <div className="homebread--items">
        {bread.map((item) => (
          <div key={item.title} className="homebread--img_container">
            <Link to="/projects" className="nav--links">
              <img
                className="homebread--img"
                src={item.image}
                alt="bread projects"
              />
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

const BreadWrapper = styled.div`
  width: 50%;
  display: flex;
  flex-direction: column;
  background-color: $primary-color-0;
`;

const rootElement = document.getElementById("root");
ReactDOM.render(<Homebread/>, rootElement);
