import projects from "../data/data";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import "../stylesheets/Projects.scss";
import React, { useState, useEffect } from "react";

export function Projects(props) {
  const [filter, setFilter] = useState("all");
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    setProjects(projects);
  }, []);

  useEffect(() => {
    setProjects([]);

    const filtered = projects.map((p) => ({
      ...p,
      filtered: p.category.includes(filter),
    }));
    setProjects(filtered);
  }, [filter]);

  return (
    <div className="projects--wrapper">
      <div className="project--filter">
        <a href="/#" active={filter === "all"} onClick={() => setFilter("all")}>
          all
        </a>
      </div>
      <div className="projects--items">
        {projects.map((item, index) => (
          <div key={index} className="projects--item">
            <div className="projects--title_text"> {item.title}</div>
            <div className="list--projects_right">
              <img
                className="list--project_img"
                src={item.image}
                alt="project"
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    project: state.selected.project,
  };
};

export default connect(mapStateToProps)(Projects);
